import React, { useState, useEffect } from "react";
import { Box, Select, Button, Text } from "@chakra-ui/core";
import StudentOutcomeList from "./StudentOutcomeList";
import "./OutcomeMapping.css";
import fakePrograms from "../../../fake-data/programs.json";

const programs = fakePrograms.programs;

const OutcomeMapping = () => {
  useEffect(() => {
    document.getElementById("top").scrollIntoView();
  });

  const [option, setOption] = useState();
  const handleSetOption = (e) => {
    setOption(e.target.value);
  };

  const renderOptions = programs.map((program, idx) => {
    return (
      <option value={program} key={idx}>
        {program}
      </option>
    );
  });

  const selectedProgram = programs.filter((program) => program == option)[0];
  console.log(selectedProgram);
  return (
    <>
      <div id="top">
        <div className="dropdown-container">
          <Box
            height="30px"
            rounded="lg"
            textAlign="center"
            bg="#38A169"
            color="white"
            fontSize="20px"
          >
            Program
          </Box>
          <Select
            mt="1em"
            id="course"
            placeholder="Select program"
            onChange={handleSetOption}
            value={option}
          >
            {/* this should be filled with a list of courses from db */}
            {renderOptions}
          </Select>
        </div>
        <div>
          {option ? <StudentOutcomeList prop={selectedProgram} /> : <div></div>}
        </div>
      </div>
    </>
  );
};

export default OutcomeMapping;
