import React, {useEffect} from 'react';
import {Box, Button, Divider, Text} from '@chakra-ui/core';
import "./EditCourseAssignments.css";
import AssignCourseInput from './AssignCourseInput';
import fakeData from "../../../fake-data/course.json";

const EditCourseAssignments = () => {
    useEffect(() => {
        document.getElementById('top').scrollIntoView();
    });

    const handleSave = (event) => {
        event.preventDefault();
        //POST the course assignments to the db
        alert("Course Assignments Saved");
    }

    const courses = fakeData.courses;
    const renderCourses = courses.map((course) => {
        return (
            <AssignCourseInput course={course.code}/>
        )
    })

    return(
        <div id='top'>
            <Box fontWeight="bold">Edit Assigned Courses</Box>
            <Box borderWidth="2px" rounded="lg" alignItems="center" m="1em" p="3em">
                <div className="course-input">
                <div className="course">
                    <Text fontSize="lg" fontWeight="bold">Course</Text>
                </div>
                <div className="coordinator">
                    <Text fontSize="lg" fontWeight="bold">Coordinator</Text>
                </div>
                <div className="section">
                    <Text fontSize="lg" fontWeight="bold">Section</Text>
                </div>
                <div className="instructor">
                    <Text fontSize="lg" fontWeight="bold">Instructor</Text>
                </div>
                <div className="trash">
                    <Text fontSize="lg" fontWeight="bold"></Text>
                </div>
            </div>
            <Divider mb='2rem' borderColor="black"/>
                {renderCourses}
                <Button variantColor="green" variant="outline" onClick={handleSave}>
                    Save
                </Button>
            </Box>
        </div>
    )
}
export default EditCourseAssignments;
