import React, { useState, useEffect } from 'react';
import {Box, Text, Button, IconButton, List, Input, ListItem} from "@chakra-ui/core";
import useInputState from '../../hooks/useInputState';
import './EditFacultyList.css';
import fakeData from "../../fake-data/faculty.json";
import FacultyMember from './FacultyMember';


const EditFacultyList = () => {
    useEffect(() => {
        document.getElementById('top').scrollIntoView();
    });

    const handleSubmit = (event) => {
        event.preventDefault();
        if (window.confirm('Are you sure you would like to submit these changes?')){
            //POST the faculty list change to the db here
            alert("Faculty List Updated!");
        }
    }

    const facultyList = fakeData.faculty;
    const adjunctList = fakeData.adjunctFaculty;
    const fellowsList = fakeData.teachingFellows;
    var cseFaculty = [];
    for (var i = 0; i< facultyList.length; i++) {
        var obj = {}; 

        obj['name'] = facultyList[i];
        obj['id'] = i;
        cseFaculty.push(obj);
    }

    var adjunct = [];
    for (var i = 0; i< adjunctList.length; i++) {
        var obj = {}; 

        obj['name'] = facultyList[i];
        obj['id'] = i;
        adjunct.push(obj);
    }

    var fellows = [];
    for (var i = 0; i< fellowsList.length; i++) {
        var obj = {}; 

        obj['name'] = facultyList[i];
        obj['id'] = i;
        fellows.push(obj);
    }

    const [faculty, setFaculty, reset] = useInputState("");
    const [facultyMembers, setFacultyMembers] = useState(cseFaculty);
    const [adjunctMembers, setAdjunctMembers] = useState(adjunct);
    const [fellowMembers, setFellowMembers] = useState(fellows);


    const addFaculty = event => {
        event.preventDefault();
        setFacultyMembers([
            ...facultyMembers,
            {
                id: facultyMembers.length,
                name: faculty,
            }
        ]);
        reset();
    }

    const removeFaculty = (id) => {
        event.preventDefault();
        setFacultyMembers(facultyMembers.filter(fac => fac.id !== id));
    } 

    const editFaculty = (newName, facID ) => {
        setFacultyMembers( facultyMembers.map(fm => 
                fm.id === facID ? {...fm, name: newName} : fm
            ) 
        );
    }

    const renderFaculty = facultyMembers.map((fac, idx) => {
        return(
            <ListItem>
                <FacultyMember member={fac.name} id={fac.id} edit={editFaculty} color={(idx % 2 == 0) ? 'gray.300' : 'gray.100'}/>
            </ListItem>
        )
    });

    const renderAdjunct = adjunctMembers.map((fac, idx) => {
        return(
            <ListItem>
                <FacultyMember member={fac.name} id={fac.id} edit={editFaculty} color={(idx % 2 == 0) ? 'gray.300' : 'gray.100'}/>
            </ListItem>
        )
    });

    const renderFellows = fellowMembers.map((fac, idx) => {
        return(
            <ListItem>
                <FacultyMember member={fac.name} id={fac.id} edit={editFaculty} color={(idx % 2 == 0) ? 'gray.300' : 'gray.100'}/>
            </ListItem>
        )
    });

    return (
        <div id='top'>
            <Text fontWeight="bold">Edit Faculty List</Text>
            <Box  alignItems="center" m="1em" p="3em">
                <div className="faculty-container">
                    <Text fontWeight="bold" mt="1em" mb="1em" fontSize="lg">Instructors/Coordinators</Text>
                    
                    <List w="70%">
                        {renderFaculty}
                    </List>
                    <Text fontWeight="bold" mt="1em" mb="1em" fontSize="lg">Adjunct Faculty</Text>
                    <List w="70%">
                        {renderAdjunct}
                    </List>
                    <Text fontWeight="bold" mt="1em" mb="1em" fontSize="lg">Teaching Fellows</Text>
                    <List w="70%">
                        {renderFellows}
                    </List>
                    </div>
                    <div className="fac-input">
                        <form onSubmit = {addFaculty}>
                                <Input mt="1rem"
                                    placeholder="enter a new faculty member" 
                                    variant="flushed" 
                                    type="text" 
                                    value={faculty} 
                                    onChange={setFaculty}
                                    w='14em'
                                />
                        </form>
                        <IconButton 
                            variant="link" 
                            isActive="false" 
                            mt="1.5em" 
                            ml="1em" 
                            variantColor="green" 
                            size='sm' 
                            icon="add"
                            onClick={addFaculty}>    
                        </IconButton>
                    </div>
                    <Button variantColor="green" variant="outline" onClick={handleSubmit}>
                        Submit
                    </Button>
            </Box>
        </div>
    )

}
export default EditFacultyList;
