import React, { useState } from 'react';
import {Box, Input, Text, Divider, List, ListItem, IconButton} from '@chakra-ui/core';
import "./AssignCourseInput.css";
import useInputState from '../../../hooks/useInputState';
import Autocomplete from "./Autocomplete.js";

import fakeData from "../../../fake-data/faculty.json";

const faculty = fakeData.faculty;

const AssignCourseInput = ({course}) => {

    const [section, setSection, reset] = useInputState("");
    const [sections, setSections] = useState([]);

    const addSection = event => {
        event.preventDefault();
        setSections([
            ...sections,
            {
                id: sections.length,
                name: section,
            }
        ]);
        reset();
    }

    const removeSection = (id) => {
        event.preventDefault();
        setSections(sections.filter(sect => sect.id !== id));

    } 

    const renderSection = sections.map(sect => {
        console.log(sect.name);
        return(
            <Box display="flex" alignItems="center">
                <div className="section-field">
                    <ListItem color="">{sect.name}</ListItem>
                </div>
                <div className="instructor-field">
                    <Autocomplete
                        suggestions={faculty}
                    />
                </div>
                <div className="trash-field">
                    <IconButton icon="delete" onClick={(e)=>{
                        e.preventDefault();
                        if (window.confirm('Are you sure you would like to delete section '+sect.name+'?')){
                            removeSection(sect.id)
                        }
                    }}></IconButton>
                </div>
            </Box>
        )
    });
    return(
        <div>
            <div className="course-input">
                <div className="course-field">
                    <Text fontSize="lg"mt="6px">{course}</Text>
                </div>
                <div className="coordinator-field">
                <Autocomplete
                    suggestions={faculty}
                />
            </div>
                <List>
                    {renderSection}
                </List>
            </div>
            <div className="section-input">
                <form onSubmit = {addSection}>
                    <Input mt="1rem"
                        placeholder="enter a new section number" 
                        variant="flushed" 
                        type="text" 
                        value={section} 
                        onChange={setSection}
                        w='13em'
                    />
                </form>
                <IconButton 
                    variant="link" 
                    isActive="false" 
                    mt="1.8em" 
                    ml=".2em"
                    variantColor="green" 
                    size='sm' 
                    icon="add"
                    onClick={addSection}>    
                </IconButton>
            </div>
            <Divider mb='2rem' mt="2rem" borderColor="black"/>
        </div>
    )
}
export default AssignCourseInput;
