import React, { useState, useEffect } from 'react';
import {Box, Select, Button} from "@chakra-ui/core";
import './FullReport.css';
import fakeData from "../../../fake-data/course.json";

const FullReport = () => {

    useEffect(() => {
        document.getElementById('top').scrollIntoView();
    });

    const courseList = fakeData.courses;
    const renderCourses = courseList.map((courses) => {
        return (
            <option value={courses.code}>
                {courses.code} {courses.name}
            </option> 
        )
    })

    return (
        <div id='top'>
            <Box borderWidth="2px" rounded="lg" alignItems="center" m="1em" p="3em">
                <div>
                    <Box fontWeight="bold">
                        Select the course, term, and year for the report you would like to generate. 
                    </Box>
                    <div className="dropdown-container">
                        <Select
                            mt = "1em"
                            id = "course"
                            placeholder="Select course">
                            {renderCourses}
                        </Select>

                        <Select mt="1em" id="term" placeholder="Select term">
                            <option value="Spring">Spring</option>
                            <option value="Summer">Summer</option>
                            <option value="Fall">Fall</option>
                        </Select>

                        <Select mt="1em" id="year" placeholder="Select year">
                            {/* should be filled with a list of years from db */}
                            <option value="option1">2020</option>
                            <option value="option2">2019</option>
                            <option value="option3">2018</option>
                            <option value="option3">2017</option>
                            <option value="option3">2016</option>
                            <option value="option3">2015</option>
                            <option value="option3">2014</option>
                            <option value="option3">2013</option>
                            <option value="option3">2012</option>
                            <option value="option3">2011</option>
                            <option value="option3">2010</option>
                        </Select>
                    </div>
                    <Button variantColor="green" variant="outline">
                        Search
                    </Button>
                </div>
            </Box>
        </div>
    )

}
export default FullReport;
