import React, { useState, useEffect } from 'react';
import {Box, Select, Button, IconButton, List, Input, ListItem} from "@chakra-ui/core";
import useInputState from '../../hooks/useInputState';
import './EditProgramList.css';
import fakeData from "../../fake-data/programs.json";

const EditProgramList = () => {
    useEffect(() => {
        document.getElementById('top').scrollIntoView();
    });

    const handleSubmit = (event) => {
        event.preventDefault();
        if (window.confirm('Are you sure you would like to submit these changes?')){
            //POST the program list change to the db here
            alert("Program List Updated!");
        }
    }

    const programList = fakeData.programs;
    var csePrograms = [];
    for (var i = 0; i< programList.length; i++) {
        var obj = {}; 

        obj['name'] = programList[i];
        obj['id'] = i;
        csePrograms.push(obj);
    }

    const [program, setProgram, reset] = useInputState("");
    const [programs, setPrograms] = useState(csePrograms);

    const addProgram = event => {
        event.preventDefault();
        setPrograms([
            ...programs,
            {
                id: programs.length,
                name: program,
            }
        ]);
        reset();
    }

    const removeProgram = (id) => {
        event.preventDefault();
        setPrograms(programs.filter(prog => prog.id !== id));
    } 

    const renderProgram = programs.map(prog => {
        return(
            <Box display="flex" alignItems="center">
                <div className="program">
                    <ListItem className="program-card">{prog.name}</ListItem>
                    <IconButton ml="1em" icon="delete" onClick={(e)=>{
                            e.preventDefault();
                            if (window.confirm('Are you sure you would like to delete '+prog.name+'?')){
                                removeProgram(prog.id)
                            }
                    }}></IconButton>
                </div>
            </Box>
        )
    });

    return (
        <div id='top'>
            <Box fontWeight="bold">Edit Program List</Box>
            <Box borderWidth="2px" rounded="lg" alignItems="center" m="1em" p="3em">
                <div className="program-container">
                    <List>
                        {renderProgram}
                    </List>
                    </div>
                    <div className="program-input">
                        <form onSubmit = {addProgram}>
                                <Input mt="1rem"
                                    placeholder="enter a new program" 
                                    variant="flushed" 
                                    type="text" 
                                    value={program} 
                                    onChange={setProgram}
                                    w='14em'
                                />
                        </form>
                        <IconButton 
                            variant="link" 
                            isActive="false" 
                            mt="1.5em" 
                            ml="1em" 
                            variantColor="green" 
                            size='sm' 
                            icon="add"
                            onClick={addProgram}>    
                        </IconButton>
                    </div>
                    <Button variantColor="green" variant="outline" onClick={handleSubmit}>
                        Submit
                    </Button>
            </Box>
        </div>
    )

}
export default EditProgramList;
