import courses from "../fake-data/course.json";
import API from "./api";

export function getCourses() {
  return courses;
}

export async function getInstructorCourses(id, term) {
  let api_helper = new API();
  let response = api_helper.getCourses(id, term.semester, term.year);

  response.then(function (result) {
    return result;
  });

  const instructorCourses = await response;
  const filteredCourses = instructorCourses.filter(
    (course) => course.instructor.id === id
  );
  return filteredCourses;
}
export async function getCoordinatorCourses(id, term) {
  let api_helper = new API();
  let response = api_helper.getCourses(id, term.semester, term.year);

  response.then(function (result) {
    return result;
  });

  const coordinatorCourses = await response;
  const filteredCourses = coordinatorCourses.filter(
    (course) => course.coordinator.id === id
  );
  return filteredCourses;
}

export async function getFormData(id, year, term, department, course, section) {
  let api_helper = new API();
  let response = api_helper.getFormBySection(
    id,
    year,
    term,
    department,
    course,
    section
  );
  response.then(function (result) {
    return result;
  });
  const formData = await response;
  return formData;
}

export async function postCoordinatorComment(
  userid,
  year,
  semester,
  department,
  courseNumber,
  coordinatorComment,
  isCourseCompleted
) {
  let api_helper = new API();
  let response = api_helper.postComment(
    userid,
    year,
    semester,
    department,
    courseNumber,
    coordinatorComment,
    isCourseCompleted
  );
  response.then(function (result) {
    return result;
  });
  const success = await response;
  console.log(success);
}

export async function postInstructorForm(
  userid,
  year,
  semester,
  department,
  courseNumber,
  sectionNumber,
  isSectionCompleted,
  outcomes,
  ITGrades,
  CSGrades,
  CEGrades
) {
  let api_helper = new API();
  let response = api_helper.postForm(
    userid,
    year,
    semester,
    department,
    courseNumber,
    sectionNumber,
    isSectionCompleted,
    outcomes,
    ITGrades,
    CSGrades,
    CEGrades
  );
  response.then(function (result) {
    return result;
  });
  const success = await response;
  console.log(success);
}

export async function login(userid, password) {
  let api_helper = new API();
  let response = api_helper.login(userid, password);
  console.log(response);
  return response.then(function (result) {
    return result;
  });
}

export async function getAllCourses(userid, semester, year) {
  let api_helper = new API();
  let response = api_helper.getAllCourses(userid, semester, parseInt(year));
  response.then(function (result) {
    return result;
  });
  const courses = await response;
  console.log(courses);
  return courses;
}

export async function getFacultyList() {
  let api_helper = new API();
  let response = api_helper.getFacultyList();
  response.then(function (result) {
    return result;
  });

  const faculty = await response;
  return faculty;
}

export async function getCoursesByDepartment(department) {
  let api_helper = new API();
  let response = api_helper.getCoursesByDepartment(department);
  response.then(function (result) {
    return result;
  });
  const courses = await response;
  return courses;
}

export async function addFacultyMember(lastName, firstName, id, type) {
  let api_helper = new API();
  let response = api_helper.addFacultyMember(firstName, lastName, id, type);
  response.then(function (result) {
    return result;
  });

  const success = await response;
  console.log(success);
}

export async function addCourse(
  userid,
  firstName,
  lastName,
  year = 0,
  semester,
  courseNumber,
  displayName,
  department
) {
  let api_helper = new API();
  let response = api_helper.addCourse(
    userid,
    firstName,
    lastName,
    (year = 0),
    semester,
    courseNumber,
    displayName,
    department
  );
  response.then(function (result) {
    return result;
  });
  const success = await response;
  console.log(success);
}
export async function removeCourse(year, semester, courseNumber, department) {
  let api_helper = new API();
  let response = api_helper.removeCourse(
    year,
    semester,
    courseNumber,
    department
  );
  response.then(function (result) {
    return result;
  });
  const success = await response;
  console.log(success);
  console.log(courseNumber);
}

export async function getCourseOutcomesbyCourse(
  year = 2020,
  semester = "",
  courseNumber = "",
  department = ""
) {
  let api_helper = new API();
  let response = api_helper.getCourseOutcomesByCourse(
    year,
    semester,
    courseNumber,
    department
  );
  response.then(function (result) {
    return result;
  });
  const courseoutcomes = await response;
  return courseoutcomes;
}
