import React, { useState, useEffect } from 'react';
import {Box, Select, Button} from "@chakra-ui/core";
import './CreateNewSemester.css';

const CreateNewSemester = () => {

    var d = new Date();
    var y = d.getFullYear();

    useEffect(() => {
        document.getElementById('top').scrollIntoView();
    });

    const handleConfirm = (event) => {
        event.preventDefault();
        if (window.confirm('Are you sure you would like to create the selected new semester?')){
            //POST the new semsester to the db
            alert("New Semester Created!");
        }
    }

    return (
        <div id='top'>
            <Box fontWeight="bold">Create New Semester</Box>
            <Box borderWidth="2px" rounded="lg" alignItems="center" m="1em" p="3em">
            <div>
                    <Box fontWeight="bold">
                        Select the term and year for the new semester. 
                    </Box>
                    <div className="dropdown-container">
                        <Select mt="1em" id="term" placeholder="Select term">
                            <option value="Spring">Spring</option>
                            <option value="Summer">Summer</option>
                            <option value="Fall">Fall</option>
                        </Select>

                        <Select mt="1em" id="year" placeholder="Select year">
                            {/* should be filled with a list of years from db */}
                            <option value="year1">{y}</option>
                            <option value="year2">{y+1}</option>
                        </Select>
                    </div>
                    <Button variantColor="green" variant="outline" onClick={handleConfirm}>
                        Confirm
                    </Button>
                </div>
            </Box>
        </div>
    )

}
export default CreateNewSemester;
