import React, {useState} from 'react';
import Navigation from '../components/instructor-components/Navigation';
import {Textarea, Button, Text} from '@chakra-ui/core';
import FormGrades from '../components/instructor-components/FormGrades';
import '../styles/coordinatorCommentInput.css'
import CoordinatorOutcomes from '../components/instructor-components/CoordinatorOutcomes';
import cookies from  'next-cookies';
import {getFormData, postCoordinatorComment} from '../api/APIHelper';
import API from '../api/api';
import Head from 'next/head';


const coordinatorCommentInput = ({form, user}) => {
    const outcomes = form.outcomes;
    const ceGrades = form.ceGrades;
    const csGrades = form.csGrades;
    const itGrades = form.itGrades;
    const course = form.section;
    console.log(course);
    console.log(form);
    console.log(course.coordinatorComment);
    const [comment, setComment] = useState("");
    const handleSubmit = (event) => {
        event.preventDefault();
        if (window.confirm('Are you sure you would like to submit this form? Once submitted, you can no longer make changes.')){
            alert("Submitted!");
            window.location = '/instructorHome';
        }
    }

    const handleSave = async (event) => {
        event.preventDefault();
        course.coordinatorComment = comment;
        course.isCourseCompleted = false; //finish later
        postCoordinatorComment(user, course);
        alert("Comment saved as:   " + form.section.coordinator.coordinatorComment);
    }
    const handleChange = (event) => {
        setComment(event.target.value);
    }


    const grades = {
        //    A,  B, C, D, F, W, I
        IT : [itGrades.a, itGrades.b, itGrades.c, itGrades.d, itGrades.f, itGrades.w, itGrades.i],
        CS : [csGrades.a, csGrades.b, csGrades.c, csGrades.d, csGrades.f, csGrades.w, csGrades.i],
        CE : [ceGrades.a, ceGrades.b, ceGrades.c, ceGrades.d, ceGrades.f, ceGrades.w, ceGrades.i],
    };


    const numberOfStudentsByMajor = {...grades};
    for (var major in grades){
        numberOfStudentsByMajor["IT"] = itGrades.totalStudents;
        numberOfStudentsByMajor["CS"] = csGrades.totalStudents;
        numberOfStudentsByMajor["CE"] = ceGrades.totalStudents;
    }

    const renderOutcomes = outcomes.map( (outcome, index) => {
        return (
            <CoordinatorOutcomes outcome={outcome.outcome} index={index}/>
        )
    })

    return(
        <>
             <Head>
                <title>UNT ABET: Coordinator Comment</title>
            </Head>
            <Navigation link="/coordinatorCommentInput"/>
            <form className="Comment">
                <div className="title">
                    <Text fontSize="lg" fontWeight="bold">{course.department} {course.courseNumber}.{course.sectionNumber} {course.displayName}</Text>
                    <Text fontSize="lg" fontWeight="bold">Instructor: {course.instructor.firstName} {course.instructor.lastName}</Text>
                <Text fontSize="lg" fontWeight="bold">Coordinator: {course.coordinator.firstName} {course.coordinator.lastName}</Text>
                </div>
                <div className="padding">
                    <FormGrades grades={grades}/>
                </div>
                {renderOutcomes}
                <div className="padding">
                    <Text fontSize="md" fontWeight="bold">Please enter any comments below and then submit or save your progress.</Text>
                    <Textarea className="Comment" value={comment} onChange={handleChange}>
                        {comment}
                    </Textarea>
                </div>
                <div className="buttons-container">
                    <Button variantColor="green" onClick={handleSave} type="save">Save Progress</Button>
                    <Button variantColor="green" onClick={handleSubmit} type="submit">Submit</Button>
                </div>
            </form>
            
        </>
    )
}

coordinatorCommentInput.getInitialProps = async ctx => {
    const getCookies = cookies(ctx);
    const section = getCookies.section;
    const course = getCookies.course;
    new API().setToken(getCookies.token); //token will be blank in the request in getInitialProps without this call
    return{
        user: getCookies.user,
        form: await getFormData(getCookies.user, 2020, "FALL", "CSCE", course, section)
    }
};


export default coordinatorCommentInput;
