import React, { useState } from 'react';
import {Box, FormControl, FormLabel, Input, Button, Text} from '@chakra-ui/core';
import '././LoginBox.css';
import useInputState from '../hooks/useInputState';
import { fakeLogin } from '../utils/fakeLogin';
import Link from 'next/link';
import API from '../api/api';
import {login} from '../api/APIHelper';

const test = async () =>{
    await API.testConnection().then(result => console.log(result));
}
const LoginBox = () => {
    let api_helper = new API();
    const test = () => {
        new API().postCourseOutcomes([
            {
                order: 1,
                outcome: "RHERE",
                mappedStudentOutcomes: [1, 0, 0, 1, 1]
            },
            {
                order: 1,
                outcome: "sdfsdfsdfsdfsdf",
                mappedStudentOutcomes: [1, 0, 0, 1, 1]
            }
        ]).then(result => console.log(result));
    }
    // //---getFormsBySection(userid, year, semester, department, course, section)---
    // let test_response = api_helper.getFormsBySection("MT2020", 2020, "Fall", "CSCE", 1030, 1);

    // //console.log(test_response); // Promise { <pending> }

    // test_response.then(function (result) {
    //     console.log(result) // "List of forms"

    //     let result_form = result[0];    // first form from list of forms

    //     let test_response2 = api_helper.postComment("MT2020", 2020, "Fall", result_form);

    //     test_response2.then(function (result2) {
    //         console.log(result2);
    //     });
    // });

    const [username, setUsername, resetUsername] = useInputState("");
    const [password, setPassword, resetPassword] = useInputState("");
    
    const handleSubmit = async (evt) => {
        evt.preventDefault();
        
        try {
            //await fakeLogin(username, password);
            console.log(username, password);
            const response = await login(username, password);

            if (response) {
                alert("success");
                console.log(response);
            }
            else 
                alert("fail");
        } catch (error) {
            alert("try 'test' & 'pass'");
        }
        finally{
            resetUsername();
            resetPassword();
        }
    }

    return(
    <Box className = "Login-Box">
        <Text fontSize="xl" className="Login">Login</Text>
            <form onSubmit={handleSubmit}>    
                <FormControl isRequired>
                    <FormLabel htmlFor="username" margin = "1rem" mt="3rem">Username</FormLabel>
                    <Input placeholder="EUID" id="input" value={username} onChange={setUsername}/>
                </FormControl>

                <FormControl isRequired>
                    <FormLabel htmlFor="password" margin = "1rem">Password</FormLabel>
                    <Input placeholder="Password" id="input" type="password" value={password} onChange={setPassword}/>
                </FormControl>

                <Button variantColor="green" margin = "1rem" onClick = {handleSubmit} type="submit">Sign In</Button>
                <Link href="/instructorHome">
                    <Button variantColor="blue" >Instructor Home</Button>
                </Link>

                <Link href="/adminHome">
                    <Button variantColor="blue" margin = "1rem">Admin Home</Button>
                </Link>
            </form>
            <button onClick={test}>TEST</button>
      </Box>
    )
}

export default LoginBox;
