import React from "react";
import { Button, Text } from "@chakra-ui/core";
import cookies from "next-cookies";

import "../styles/formCompletion.css";
import OutcomeInput from "../components/instructor-components/OutcomeInput";
import FormGrades from "../components/instructor-components/FormGrades";
import Navigation from "../components/instructor-components/Navigation";
import API from "../api/api";
import { getFormData, postInstructorForm } from "../api/APIHelper";
import Head from "next/head";

const formCompletion = ({ form }) => {
  console.log(form);
  /*
    const outcomes = form.outcomes;
    const ceGrades = form.ceGrades;
    const csGrades = form.csGrades;
    const itGrades = form.itGrades;
    

  const course = form.section;
  */
  //console.log(form);
  const grades = {
    //    A,  B, C, D, F, W, I
    IT: [
      itGrades.a,
      itGrades.b,
      itGrades.c,
      itGrades.d,
      itGrades.f,
      itGrades.w,
      itGrades.i,
    ],
    CS: [
      csGrades.a,
      csGrades.b,
      csGrades.c,
      csGrades.d,
      csGrades.f,
      csGrades.w,
      csGrades.i,
    ],
    CE: [
      ceGrades.a,
      ceGrades.b,
      ceGrades.c,
      ceGrades.d,
      ceGrades.f,
      ceGrades.w,
      ceGrades.i,
    ],
  };

  const numberOfStudentsByMajor = { ...grades };
  for (var major in grades) {
    numberOfStudentsByMajor["IT"] = itGrades.totalStudents;
    numberOfStudentsByMajor["CS"] = csGrades.totalStudents;
    numberOfStudentsByMajor["CE"] = ceGrades.totalStudents;
  }

  const renderOutcomes = outcomes.map((outcome, index) => {
    console.log(outcome.studentWorks.length);

    return (
      <OutcomeInput
        outcome={outcome.outcome}
        currentStudentWorks={outcome.studentWorks}
        index={index}
        studentsByMajor={numberOfStudentsByMajor}
      />
    );
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    if (
      window.confirm(
        "Are you sure you would like to submit this form? Once submitted, you can no longer make changes."
      )
    ) {
      alert("Submitted!");
      window.location = "/homePage";
    }
  };

  const handleSave = (event) => {
    event.preventDefault();
  };

  return (
    <div>
      <Head>
        <title>UNT ABET: Form Completion</title>
      </Head>
      <Navigation link="/formCompletion" />
      <div className="title">
        <Text fontSize="xl" mb="1em" fontWeight="bold">
          {course.department} {course.courseNumber}.{course.sectionNumber}{" "}
          {course.displayName}
        </Text>
        <Text fontSize="lg" fontWeight="bold">
          Instructor: {course.instructor.firstName} {course.instructor.lastName}
        </Text>
        <Text fontSize="lg" fontWeight="bold">
          Coordinator: {course.coordinator.firstName}{" "}
          {course.coordinator.lastName}
        </Text>
        <div className="coordinator-comment" fontSize="lg" fontWeight="bold">
          <Text fontSize="lg" fontWeight="bold" textAlign="center">
            Coordinator comments
          </Text>
          <Text fontSize="lg">{course.coordinatorComments}</Text>
        </div>
      </div>
      <FormGrades grades={grades} />
      {renderOutcomes}
      <div className="buttons-container">
        <Button variantColor="green" onClick={handleSave} type="save">
          Save Progress
        </Button>
        <Button variantColor="green" onClick={handleSubmit} type="submit">
          Submit
        </Button>
      </div>
    </div>
  );
};

formCompletion.getInitialProps = async (ctx) => {
  const getCookies = cookies(ctx);
  new API().setToken(getCookies.token); //token will be blank in the request in getInitialProps without this call
  const section = getCookies.section;
  const course = getCookies.course;
  return {
    user: getCookies.user,
    section,
    course,
    form: await getFormData(
      getCookies.user,
      2020,
      "FALL",
      "CSCE",
      course,
      section
    ),
  };
};

export default formCompletion;
