import {Text, Button, Link, Box } from "@chakra-ui/core"; 

import CourseCard from './CourseCard';
import './FormsView.css'

const FormsView = ({term, instructorCourses, coordinatorCourses}) => {

    instructorCourses.sort(
        (a, b) => (a.courseNumber + a.sectionNumber > b.courseNumber + b.sectionNumber)  ? 1 : -1
    );
    coordinatorCourses.sort(
        (a, b) => (a.courseNumber + a.sectionNumber > b.courseNumber+ b.sectionNumber) ? 1 : -1
    )
    
    const handleClick = async (course, section) => {
        document.cookie = `course=${course}; path=/`;
        document.cookie = `section=${section}; path=/`;
    }
    const renderInstructorCourses = instructorCourses.map(course => {
        return(
            <div>
                <CourseCard code={course.courseNumber} section={course.sectionNumber} instructor={course.instructor} coordinator={course.coordinator} name={course.displayName}/>
                {/* <Link href="/formCompletion">
                    {   
                        course.isCourseCompleted ? <Button fontWeight="bold" rightIcon="check" variantColor="teal" mt="1em" isDisabled="true" w="100%" size="sm">Form Complete</Button> : <Button size="sm" variantColor="green" mt="1em" w="100%" size="sm" onClick={
                            alert('test')
                        }>Work On ABET Form</Button>
                    }
                </Link> */}
                <Button size="sm" variantColor="green" mt="1em" w="100%" size="sm" onClick={e => {
                    e.preventDefault();
                    handleClick(course.courseNumber, course.sectionNumber);
                    window.location.href = 'http://localhost:3000/formCompletion';
                }}>
                    Work On ABET Form
                </Button>
            </div>
        )
    })
    const renderCoordinatorCourses = coordinatorCourses.map(course => {
        return(
            <div>
                <CourseCard code={course.courseNumber} section={course.sectionNumber} instructor={course.instructor} coordinator={course.coordinator} name={course.displayName}/>
                <Button size="sm" variantColor="green" mt="1em" w="100%" size="sm" onClick={e => {
                    e.preventDefault();
                    handleClick(course.courseNumber, course.sectionNumber);
                    window.location.href = 'http://localhost:3000/coordinatorCommentInput';
                }}>
                    Add Comment
                </Button>
            </div>
        )
    })
    return(
        <div className="course-view">
            <Text fontSize="lg" fontWeight="bold" textAlign="left">Selected Term: {term.semester} {term.year}</Text>
            <div>
                <Box mt="2em">
                    <Text fontWeight="bold" textAlign="left" mb="1em">Instructor Courses</Text>
                    <div className="forms-grid">
                         {renderInstructorCourses}
                    </div>
                </Box>
                
                <Box mt="2em">
                    <Text fontWeight="bold" textAlign="left" mb="1em">Coordinator Courses</Text>
                    <div className="forms-grid">
                         {renderCoordinatorCourses}
                    </div>
                </Box>
            </div>
        </div>
    )
}



export default FormsView;
