import React, { useState, useEffect } from 'react';
import {Box, Text, Button, IconButton, List, Input, ListItem} from "@chakra-ui/core";
import useInputState from '../../hooks/useInputState';
import './EditFacultyList.css';
import fakeData from "../../fake-data/faculty.json";
import FacultyMember from './FacultyMember';


const EditFacultyList = () => {
    useEffect(() => {
        document.getElementById('top').scrollIntoView();
    });

    const handleSubmit = (event) => {
        event.preventDefault();
        if (window.confirm('Are you sure you would like to submit these changes?')){
            //POST the faculty list change to the db here
            alert("Faculty List Updated!");
        }
    }

    const facultyList = fakeData.faculty;
    var cseFaculty = [];
    for (var i = 0; i< facultyList.length; i++) {
        var obj = {}; 

        obj['name'] = facultyList[i];
        obj['id'] = i;
        cseFaculty.push(obj);
    }

    const [faculty, setFaculty, reset] = useInputState("");
    const [facultyMembers, setFacultyMembers] = useState(cseFaculty);

    const addFaculty = event => {
        event.preventDefault();
        setFacultyMembers([
            ...facultyMembers,
            {
                id: facultyMembers.length,
                name: faculty,
            }
        ]);
        reset();
    }

    const removeFaculty = (id) => {
        event.preventDefault();
        setFacultyMembers(facultyMembers.filter(fac => fac.id !== id));
    } 

    const renderFaculty = facultyMembers.map((fac, idx) => {
        
        return(
            // <Box display="flex" alignItems="center">
            //     <div className="faculty">
            //         <ListItem className="faculty-card">{fac.name}</ListItem>
            //         <IconButton ml="1em" icon="delete" onClick={(e)=>{
            //             e.preventDefault();
            //             if (window.confirm('Are you sure you would like to delete '+fac.name+'?')){
            //                 removeFaculty(fac.id)
            //             }
            //         }}></IconButton>
            //     </div>
            // </Box>
            <FacultyMember member={fac.name} color={(idx % 2 == 0) ? 'gray.300' : 'gray.100'}/>
        )
    });

    return (
        <div id='top'>
            <Text fontWeight="bold">Edit Faculty List</Text>
            <Box  alignItems="center" m="1em" p="3em">
                <div className="faculty-container">
                    <List w="90%">
                        {renderFaculty}
                    </List>
                    </div>
                    <div className="fac-input">
                        <form onSubmit = {addFaculty}>
                                <Input mt="1rem"
                                    placeholder="enter a new faculty member" 
                                    variant="flushed" 
                                    type="text" 
                                    value={faculty} 
                                    onChange={setFaculty}
                                    w='14em'
                                />
                        </form>
                        <IconButton 
                            variant="link" 
                            isActive="false" 
                            mt="1.5em" 
                            ml="1em" 
                            variantColor="green" 
                            size='sm' 
                            icon="add"
                            onClick={addFaculty}>    
                        </IconButton>
                    </div>
                    <Button variantColor="green" variant="outline" onClick={handleSubmit}>
                        Submit
                    </Button>
            </Box>
        </div>
    )

}
export default EditFacultyList;
