import {useState} from 'react';
import {Button, Select, Text, Box} from '@chakra-ui/core';

import {getAllCourses, getInstructorCourses} from '../../api/APIHelper';
import CourseList from './CourseList';

const GenerateSimpleReport = ({user}) => {
    const [term, setTerm] = useState({
        semester:'',
        year: null,
    });
    const [courses, setCourses] = useState();
    const terms = [
        {
            semester:'Spring',
            year: 2020
        },
        {
            semester:'Summer',
            year: 2020
        },
        {
            semester:'Fall',
            year: 2020
        }
    ];
    const handleSetTerm = async (e) =>{
        setTerm({ 
            semester: e.target.value.split(' ')[0],
            year: e.target.value.split(' ')[1]
        })
        console.log(e.target.value.split(' ')[1]);
        console.log(term.year);
        const coursesRes = await getAllCourses(user, "fall", 2020);
        console.log(coursesRes);
        setCourses(coursesRes);
    }

    return (
        <Box borderWidth="2px" rounded="lg" alignItems="center" m="1em" p="3em">
            <Text fontWeight="bold" fontSize="lg" mb="1em">Find ABET Report</Text>
            {
                (term.year == null) ? 
                <Select placeholder="Choose Semester" w="70%" m="0 auto" onChange={handleSetTerm}>
                    <option value={terms[0].semester + " " + terms[0].year.toString(10)}>{terms[0].semester} {terms[0].year}</option>
                    <option value={terms[1].semester + " " + terms[1].year.toString(10)}>{terms[1].semester} {terms[1].year}</option>
                    <option value={terms[2].semester + " " + terms[2].year.toString(10)}>{terms[2].semester} {terms[2].year}</option>
                </Select>
                :
                
                <div>
                    <Text fontWeight="bold">{term.semester} {term.year}</Text>
                    <CourseList courses={courses}/>
                </div>
            }
        </Box>
    )
};

export default GenerateSimpleReport;
