import React, { useState, useEffect } from "react";
import { Box, Select, Button, Text } from "@chakra-ui/core";
import OutcomeList from "./EditOutcomeList";
import "./EditCourseOutcomes.css";
import fakeData from "../../fake-data/course.json";

const courses = fakeData.courses;

console.log(courses);

const EditCourseOutcomes = () => {
  useEffect(() => {
      document.getElementById('top').scrollIntoView();
  });

  const [option, setOption] = useState();
  const handleSetOption = (e) => {
    setOption(e.target.value);
  };

  console.log(option);

  const renderOptions = courses.map((course) => {
    return (
      <option value={course.code}>
        {course.code} {course.name}
      </option>
    );
  });

  const selectedCourse = courses.filter((course) => course.code == option)[0];

  console.log(selectedCourse);

  return (
    <>
      <div id='top'>
        <div className="dropdown-container">
          <Box
            height="30px"
            rounded="lg"
            textAlign="center"
            bg="#38A169"
            color="white"
            fontSize="20px"
          >
            Course
          </Box>
          <Select
            mt="1em"
            id="course"
            placeholder="Select course"
            onChange={handleSetOption}
            value={option}
          >
            {/* this should be filled with a list of courses from db */}
            {renderOptions}
          </Select>
        </div>
        <div>
          {option ? <OutcomeList prop={selectedCourse} /> : <div></div>}
        </div>
      </div>
    </>
  );
};

export default EditCourseOutcomes;
